import React from 'react';
import { Country } from './countriesList';












interface CountryDetailsprops{ 
  isDarkMode: boolean;
  country: Country | null;
  error: str 
}



const CountryDetails: React.FC<CountryDetailsprops> = ({isDarkMode, country}) => {
  if (error) {
    return <div>Error fetching country data: {error}</div>;
  }
  if (!country) {
    return <div>No country data available</div>; // Handle the case when country data is not available
  }


  return(
    <div>
     <h2>{country.name.common}</h2>
      <img src={country.flags.png} alt="Flag" />
      <p>Population: {country.population.toLocaleString()}</p>
      <p>Region: {country.region}</p>
      <p>Capital: {country.capital}</p>

    </div>
  )
 
    

};

export default CountryDetails;
