import {BrowserRouter as Router, Routes, Route,useLocation  } from 'react-router-dom';
import React, {useState, useEffect} from "react";
import NavBar from "./components/navBar";
import './App.css';
import SearchBar from "./components/searchbar";
import ToggleButton from "./components/button";
import DropDown from "./components/dropDown";
import CountryList from "./components/countriesList";
import CountryDetails from "./components/countriesDetails";





function App () {
    const [isDarkMode, setIsDarkMode] = useState<boolean>(false);
    const [selectedRegion, setSelectedRegion] = useState<string | null>(null);
    const [searchCountry, setSearchCountry] = useState<string>('');
    const [countryDetailsPage, setCountryDetailsPage] = useState<boolean>(false)


    const location = useLocation();

    useEffect(() => {
      document.body.className = isDarkMode ? 'dark-theme' : 'light-theme';
    }, [isDarkMode]);

    const toggleTheme  = () => {
      setIsDarkMode(!isDarkMode);
    };

    const handleRegionSelect = (region: string | null) => {
      setSelectedRegion(region);
    };   

    useEffect(() => {
      // Check if the current path is a country details page
      setCountryDetailsPage(location.pathname.includes('/country/'));
    }, [location.pathname]);


  return (
    <div className={`App ${isDarkMode ? 'dark' : 'light'}`}> 
    <div className='header'>
    <div className="navigate"><NavBar/></div>
    <div className="click-button"><ToggleButton isDarkMode={isDarkMode} onToggle={toggleTheme}/></div> 
    </div>

    <div  className="flex">   
      <div>
    {!countryDetailsPage &&  <SearchBar isDarkMode={isDarkMode} onSearch={setSearchCountry}/>}
      </div>
    <div className="dropdown-flex">
   {!countryDetailsPage &&<DropDown isDarkMode={isDarkMode}  onRegionSelect={handleRegionSelect} />}
      </div>


    </div>
    <div>

    <Router>
      <Routes>
        <Route path='/' element={<CountryList isDarkMode={isDarkMode} selectedRegion={selectedRegion} searchCountry={searchCountry} />} />
        <Route  path='/country/:countryName' element={<CountryDetails  isDarkmode />}/>
      </Routes>
      </Router>









    </div>
     </div>






  );
}

export default App;
