import React from 'react';
import { Country } from './countriesList';












interface CountryDetailsprops{ 
  isDarkMode: boolean;
  country: Country | null; 
 
  
  

}


const CountryDetails: React.FC<CountryDetailsprops> = ({isDarkMode, country}) => {
  if (!country) {
    return <div>No country details available.</div>;
  }





  return(
    <div className={`countryDetails ${isDarkMode ? 'dark' : 'light'}`}>
      <img src={country.flags.png} alt="Flag" className='countryImg' />
      <h1>{country.name.common}</h1>
      {/* Add more details as needed */}
      <p><span>Population: </span>{country.population.toLocaleString()}</p>
      <p><span>Region:</span> {country.region}</p>
      <p><span>Capital: </span>{country.capital}</p>
      {/* Add more details as needed */}
    </div>
  )
 
    

};

export default CountryDetails;
