import React from 'react';
import { useLocation } from 'react-router-dom';

interface LocationState {
  countryDetails: Country;
}





interface CountryDetailsprops{
  isDarkMode: boolean;
  

}



const CountryDetails: React.FC<CountryDetailsprops> = ({isDarkMode, }) => {
  const location = useLocation<LocationState>();
  const countryDetails = location.state?.countryDetails;
  


  if (!countryDetails) {
    // Handle the case where there are no country details
    return <div>No country details available.</div>;
  }


  

  


 
 


  return (
    <div></div>
  )
    

};

export default CountryDetails;
