import React from 'react';












interface CountryDetailsprops{ 
  isDarkMode: boolean;
  countryDetails: Country | null;


}



const CountryDetails: React.FC<CountryDetailsprops> = ({isDarkMode, countryDetails}) => {
  if (!countryDetails) {
    return <div>Loading...</div>;
  }





  return(
    <div>
      <h2>Country Details for {countryDetails.name.common}</h2>


    </div>
  )
 
    

};

export default CountryDetails;
