import React, { useEffect, useState } from 'react';
import { useParams} from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';










interface CountryDetailsprops{ 
  isDarkMode: boolean;
 
  
  

}


const CountryDetails: React.FC<CountryDetailsprops> = ({isDarkMode}) => {
  const { countryName } = useParams<{ countryName: string }>();
  const [countryDetails, setCountryDetails] = useState<any>(null);
  const [borderCountryNames, setBorderCountryNames] = useState<Country[]>([]);


  const navigate = useNavigate();

  useEffect(() => {
    const storedCountry = localStorage.getItem('selectedCountry');

    if (storedCountry) {
      const countryDetails = JSON.parse(storedCountry);
      setCountryDetails(countryDetails);

      // Fetch border country names dynamically from the REST Countries API
      if (countryDetails.borders.length > 0) {
        fetch(`https://restcountries.com/v3.1/alpha?codes=${countryDetails.borders.join(',')}`)
          .then((response) => response.json())
          .then((borderCountries) => {
            const names = borderCountries.map((borderCountry: any) => borderCountry.name.common);
            setBorderCountryNames(names);
          })
          .catch((error) => console.error('Error fetching border country names:', error));
      }
    }
  }, [countryName]);
  





  

  return (
    <div>
       <div>
      <button className={`back-button ${isDarkMode ? 'dark' : 'light'}`} onClick={() => navigate(-1)} ><FontAwesomeIcon icon={faArrowLeft} style={{ marginRight: '8px' }}/>
      Back</button>
      </div>
      {countryDetails && (
        <div>
          <div className='center'>  
    <article className='country-details'>
       <div><img id ='flag' src={countryDetails.flags.svg} alt='Flag'/></div>
        <div >
          <h2 id='details-header'>{countryDetails.name.common}</h2>
          <div className='country-details-wrapper'>
        <div className='country-details-1'>
     <p><span>Native Name: </span> 
        {countryDetails.name.nativeName ? (countryDetails.name.nativeName[Object.keys(countryDetails.name.nativeName)[0]] && countryDetails.name.nativeName[Object.keys(countryDetails.name.nativeName)[0]].common)  : (
        countryDetails.name.common
      )} </p>
      <p><span>Population:</span> {countryDetails.population.toLocaleString()}</p>
      <p><span>Region:</span> {countryDetails.region}</p>
      <p><span>Sub Region:</span> {countryDetails.subregion}</p>
      <p><span>Capital:</span> {countryDetails.capital}</p> 
      </div>
    <div className='country-details-2'>
    
  
    <p><span>Top Level Domain:</span>  {Array.isArray(countryDetails.tld) && countryDetails.tld.length > 0
    ? countryDetails.tld[0]
    : ''
  }
</p>
         <p><span>Currencies:</span> {countryDetails.currencies?.[Object.keys(countryDetails.currencies)[0]]?.name || ''}</p>
          <p><span>Languages:</span> {Object.values(countryDetails.languages || {}).join(', ')}</p> 
          </div>
          </div>
          <p id='border'>

  <span>Borders: </span>
  {countryDetails && (
        <div>
          {/* ... */}
          <div id="border">
            <span>Borders: </span>
            {borderCountryNames.length > 0 ? (
              <ul>
                {borderCountryNames.map((borderCountryName) => (
                  <li key={borderCountryName}>{borderCountryName}</li>
                ))}
              </ul>
            ) : (
              <p>No bordering countries</p>
            )}
          </div>
        </div>
      )}

    </p>



       

          </div>

       </article>

        </div>
        
        </div>
      )}
  
    </div>
  )
    

};

export default CountryDetails;
